export function getSectionImage(postIndex: number, sectionIndex: number): string {
  return sectionImages[(postIndex * 7 + sectionIndex * 3) % sectionImages.length];
}

function isPublished(post: BlogPost): boolean {
  return post.publishDate <= new Date().toISOString().split('T')[0];
}

export function getPublishedPosts(): BlogPost[] {
  return blogPosts.filter(isPublished);
}

export function getBlogPost(slug: string): BlogPost | undefined {
  const post = blogPosts.find((p) => p.slug === slug);
  return post && isPublished(post) ? post : undefined;
}

export function getRelatedPosts(post: BlogPost): BlogPost[] {
  return post.relatedSlugs
    .map((slug) => blogPosts.find((p) => p.slug === slug))
    .filter((p): p is BlogPost => p !== undefined && isPublished(p));
}
